import React, { Component } from 'react';
import '../index.css';

class Calculator extends Component {
    constructor(){
        super();
        this.state={finalexp : ""}

        this.handleClick = this.handleClick.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }
    handleClick = (value)=> {
        this.setState({finalexp: this.state.finalexp.setState(this.state.finalexp+=value)})
    }
    handleInputChange = (e) => {
        this.setState({
            expression: e.target.value
        });
    }
render() {
return (
<div>
<table border="1"> 
         <tr> 
            <td colspan="3"><input type="text" id="result" onChange={ this.handleChange } value= {this.state.finalexp}/></td> 

            <td><input type="button" value="c" onclick="clr()"/> </td> 
         </tr> 
         <tr> 
            <td><input type="button" value="1" onclick={this.handleClick('1')}/> </td> 
            <td><input type="button" value="2" onclick={this.handleClick('2')}/> </td> 
            <td><input type="button" value="3" onclick="dis('3')"/> </td> 
            <td><input type="button" value="/" onclick="dis('/')"/> </td> 
         </tr> 
         <tr> 
            <td><input type="button" value="4" onclick="dis('4')"/> </td> 
            <td><input type="button" value="5" onclick="dis('5')"/> </td> 
            <td><input type="button" value="6" onclick="dis('6')"/> </td> 
            <td><input type="button" value="-" onclick="dis('-')"/> </td> 
         </tr> 
         <tr> 
            <td><input type="button" value="7" onclick="dis('7')"/> </td> 
            <td><input type="button" value="8" onclick="dis('8')"/> </td> 
            <td><input type="button" value="9" onclick="dis('9')"/> </td> 
            <td><input type="button" value="+" onclick="dis('+')"/> </td> 
         </tr> 
         <tr> 
            <td><input type="button" value="." onclick="dis('.')"/> </td> 
            <td><input type="button" value="0" onclick="dis('0')"/> </td> 
            <td><input type="button" value="=" onclick="solve()"/> </td> 
            <td><input type="button" value="*" onclick="dis('+')"/> </td> 
         </tr> 
      </table> 
</div>
);
}
}
export default Calculator;
