import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import {Redirect} from 'react-router';
import Grid from '@material-ui/core/Grid';
import MiniDrawer from '../Layout/MiniDrawer';
//import { getProfile } from "../../actions";
//import { updateProfile } from "../../actions";
import { connect } from 'react-redux';
import {graphql, compose} from 'react-apollo';
import {profile} from '../../queries/queries';
import {updateProfile} from '../../mutations/mutations';
import {withApollo} from 'react-apollo';
const axios = require('axios');




class Profile extends Component 
{
  constructor(props)
  {
    super(props);
    this.state = {
        name: "",
        email: "",
        phone:"",
        aboutme:"",
        city:"",
        country:"",
        company:"",
        school:"",
        hometown:"",
        languages:"",
        submitsuccess:false,
        loggedout:false,
        profilechange:false,
        image_path:'',
        profilePicView:'',
        profilepic:''
       // male:"",
        //female:""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
    this.handlePhotoChange= this.handlePhotoChange.bind(this);
  }
  handlePhotoChange() {
    this.setState({ profilechange: true });
  }
  componentDidMount(){
    //this.props.getProfile();
    localStorage.setItem("Email" ,  "supriyajain5230@gmail.com")
    this.props.client.query({
        query : profile,
        variables: {
            Email : localStorage.getItem("Email")
        },
    }
    ).then((response)=>{
        console.log('Response profile', response.data);
        const result = response.data.profile;
        this.setState({
            name: response.data.profile.Name,
            email: response.data.profile.Email,
            phone: response.data.profile.Phone,
            aboutme:  response.data.profile.Aboutme,
            city: response.data.profile.City,    
            country: response.data.profile.Country,
            company:  response.data.profile.Company,
            school: response.data.profile.School,
            hometown: response.data.profile.Hometown,
            languages: response.data.profile.Languages,
            success:response.data.profile.Status,
            profilepic: response.data.profile.ProfileImage,
            //profilePicView: nextProps.profiledetails.profiledetails.imageData
        });



        });
    

    console.log('data:', this.props.data);
  }
  /*componentWillReceiveProps(nextProps) {
  
    if (nextProps.profiledetails) {
        this.setState({
            name: response.data.profile.Name,
            email: response.data.profile.Email,
            phone: response.data.profile.Phone,
            aboutme:  response.data.profile.Aboutme,
            city: response.data.profile.City,    
            country: response.data.profile.Country,
            company:  response.data.profile.Company,
            school: response.data.profile.School,
            hometown: response.data.profile.Hometown,
            languages: response.data.profile.Languages,
            success:response.data.profile.Status,
            profilepic: response.data.profile.ProfileImage,
            profilePicView: nextProps.profiledetails.profiledetails.imageData
        });
    }
  }*/
  handleChange(event)
  {
    const target = event.target;
    const name = target.name;
    if (name === "profilepic") {
        console.log(target.files);
        var profilePhoto = target.files[0];
        var data = new FormData();

        data.append('photos', profilePhoto);
        console.log(data)
        axios.defaults.withCredentials = true;
        axios.post('/users/upload-file', data)
            .then(res => {
                if (res.status === 200) {
                    console.log('Profile Photo Name: ', profilePhoto.name);

                    //Download image
                    axios.post('/users/download-file/' + profilePhoto.name)
                        .then(res => {
                            let preview = 'data:image/jpg;base64, ' + res.data;
                            this.setState({
                                profilepic: profilePhoto.name,
                                profilePicView: preview
                            })

                        }).catch((err) =>{
                            if(err){
                                this.setState({
                                    errorRedirect: true
                                })
                            }
                        });
                }
            });
    }
    else{
        const {name, value} = event.target;
        this.setState({ [name]: value });
    }
  }

  handleLogout = () => {
    axios.defaults.withCredentials = true;
    axios.post('/users/logout')
        .then(response => {
            if (response.status === 200) {
                console.log('User logged out successfully!');
                this.setState ( {
                    loggedout:true,
                        });
            }
        });
}
  handleSubmit(event)
  {
    event.preventDefault();
    const data ={
        name: this.state.name,
        email: this.state.email,
        phone: this.state.phone,
        aboutme: this.state.aboutme,
        city: this.state.city,
        country: this.state.country,
        company: this.state.company,
        school: this.state.school,
        hometown: this.state.hometown,
        languages: this.state.languages,
        profilepic: this.state.profilepic
       // male:"",
        //female:""
        
    };
    this.props.updateProfile(data);
    this.props.getProfile(data);
  }

  render() {
    /*let redirectVar = null;
    if(this.state.loggedout){
        redirectVar = <Redirect to= "/login"/>
      }
     else  if(this.state.submitsuccess){
        redirectVar = <Redirect to= "/teacherdashboard"/>
